    methods: {
        open() {
            this.animation = true

            setTimeout(() => {
                this.showGlow = true
            }, 1600);

            setTimeout(() => {
                this.showCookie = true
            }, 1900);

            setTimeout(() => {
                this.showCookie = false
            }, 2900);

            setTimeout(() => {
                this.animation = false
                this.show = true
