                let number = Math.round(Math.random() * (30 - 1) + 1)
                this.cookies = cookies.find(cookie => cookie.id === number).text
            }, 3200);
        },
        openAgane() {
            this.showGlow = false
            this.showText = false
            this.showGlow = false

            setTimeout(() => {
                this.animation = true
